rch">                <tr class="active">                    <th class="text-center" width="10%">STT</th>                    <th style="width:18%">Mã hóa đơn</th>                    <th style="width: 15%">Thời gian</th>                    <th style="width: 16%">Tổng tiền</th>                    <th style="width: 13%">Đã thanh toán</th>                    <th>Còn lại</th>                    <th class="text-center" style="width: ">Trạng thái đơn hàng</th>                </tr>                <?php if(count($lists)) : $stt = 0; $tong = 0;$thanhtoan = 0;$conlai = 0; ?>                    <?php foreach($lists as $list) : $stt +=1;?>                        <?php                            $tong += $list->total_bill;                            $thanhtoan += $list->payted;                            $conlai += $list->total_bill - $list->payted;                        ?>                        <tr>                            <td class="text-center"><?=$stt;?></td>                            <td>                                <p class="blue_txt">                                    <a href="<?=base_url('vnadmin/order/detail?id='.base64_encode($list->id));?>" style="color:#316ee2;font-weight: bold">                                        <?=$list->code;?>                                    </a>                                </p>                            </td>                            <td>                                <?=date('d/m/Y',$list->time);?>                            </td>                            <td>                                <span class="red_txt"><?=number_format($list->total_bill);?> VNĐ</span>                            </td>                            <td class="green_txt">                                <?=number_format($list->payted);?> VNĐ                            </td>                            <td>                                <span class="red_txt"><?=number_format($list->total_bill - $list->payted);?> VNĐ</span>                            </td>                            <td class="text-center">                                <?php if($list->status == 1) : ?><span class="btn btn-xs btn-default">Khởi tạo<span><?php endif;?>                                        <?php if($list->status == 2) : ?><span class="btn btn-xs btn-success">Đã duyệt<span><?php endif;?>                                                <?php if($list->status == 3) : ?><span class="btn btn-xs btn-success">Đã đặt cọc<span><?php endif;?>                                                        <?php if($list->status == 4) : ?><span class="btn btn-xs btn-success">Đã đặt hàng<span><?php endif;?>                                                                <?php if($list->status == 5) : ?><span class="btn btn-xs btn-success">Đã quyết toán<span><?php endif;?>                                                                        <?php if($list->status == 6) : ?><span class="btn btn-xs btn-success">Hoàn thành<span><?php endif;?>                                                                                <?php if($list->status == 7) : ?><span class="btn btn-xs btn-success">Đã hủy<span><?php endif;?>                                                                                        <?php if($list->status == 8) : ?><span class="btn btn-xs btn-success">Hết hàng<span><?php endif;?>                            </td>                        </tr>                    <?php endforeach;?>                    <tfoot>                        <tr>                            <td colspan="3" class="text-right">                                <strong>Tổng</strong>                            </td>                            <td><strong class="blue_txt"><?=number_format($tong);?> VNĐ</strong></td>                            <td><strong class="blue_txt"><?=number_format($thanhtoan);?> VNĐ</strong></td>                            <td><strong class="blue_txt"><?=number_format($conlai);?> VNĐ</strong></td>                            <td></td>                        </tr>                    </tfoot>                <?php endif;?>            </table>        </div>        <div class="clearfix-5"></div>    </div></div><style>    .content-wrapper{        background-color:#fff !important;    }</style><script type="text/javascript">    $("#from").datepicker({        dateFormat: 'dd/mm/yy',    });    $("#to").datepicker({        dateFormat: 'dd/mm/yy',    });</script>